import React from 'react'
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types'
import RandomWords from 'random-words'

export default class Input extends React.Component{
    constructor(){
        super();
        this.state = {
            one : RandomWords({exactly: 15, minLength: 3, maxLength: 5}),
            two : RandomWords({exactly: 15, minLength: 4, maxLength: 9}),
            three : RandomWords({exactly: 15, minLength: 3, maxLength: 6}),
            four : RandomWords({exactly: 15, minLength: 4, maxLength: 9}),
            five : RandomWords({exactly: 15, minLength: 5, maxLength: 10}),
            input: ""
        };
    }

    changeHandler(){
        this.setState({input:event.target.value});
    }

    render(){
        console.log(this.state.input)
        return(
            <div>
                <div>display div</div>
                <input onChange={()=>{this.changeHandler()}} value={this.state.input}/>
            </div>
            )
    }
}
