import React from 'react'
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types'
import RandomWords from 'random-words'

export default class DisplayWord extends React.Component{
    constructor(){
        super();
        this.state = {
            alphabets : []
        };
    }

    componentDidMount() {
        this.getAlphabets();
    }

    getAlphabets() {
        fetch('/games/merdeaf.json')
        .then(res=>res.json())
        .then(result=>this.setState({alphabets: result}));
    }

    render(){
        let display = "";
        let word = this.props.word;
        let letters = word.split("");
        let splitword = letters.map((letter, index)=>{
            return(<div>{letter}</div>);
        });

        if (this.state.alphabets[0] !== undefined) {
            display = <h1>{this.state.alphabets[0].link}</h1>
            return display
        }

        return(
            <div>
                <button>Start Game</button>
                <div>{splitword}</div>
            </div>
            )
    }
}
