import React from 'react'
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types'
import RandomWords from 'random-words'

export default class DisplayWord extends React.Component{
    constructor(){
        super();
        this.state = {
            input: "",
            one : RandomWords({exactly: 15, minLength: 3, maxLength: 5}),
            two : RandomWords({exactly: 15, minLength: 4, maxLength: 9}),
            three : RandomWords({exactly: 15, minLength: 3, maxLength: 6}),
            four : RandomWords({exactly: 15, minLength: 4, maxLength: 9}),
            five : RandomWords({exactly: 15, minLength: 5, maxLength: 10})
        };
    }

    getword(){
        if(this.props.stage === 1){
            console.log(this.state.one);
        }else if (this.props.stage === 2){
            console.log(this.state.two);
        }else if (this.props.stage === 3){
            console.log(this.state.three)
        }else if (this.props.state === 4){
            console.log(this.state.four)
        }else{
            console.log(this.state.five)
        }
    }

    render(){
        getword()
        return(
            <div>
                <div>yippee display</div>
            </div>
            )
    }
}
