import React from 'react'
import ReactDOM from 'react-dom'
import PropTypes from 'prop-types'
import RandomWords from 'random-words'

export default class DisplayWord extends React.Component{
    constructor(){
        super();
        this.state = {
            alphabets : []
        };
    }

    componentDidMount() {
        this.getAlphabets();
    }

    getAlphabets() {
        fetch('/games/merdeaf.json')
        .then(res=>res.json())
        .then(result=>this.setState({alphabets: result}));
    }

    render(){
        let display = "";
        let word = this.props.word;
        let letters = word.split("");
        var indexes = {a: 0, b: 1, c: 2, d: 3, e: 4, f: 5, g: 6, h: 7, i: 8, j: 9, k: 10, l: 11, m: 12, n: 13, o: 14, p: 15, q: 16, r: 17, s: 18, t: 19, u: 20, v: 21, w: 22, x: 23, y: 24, z: 25};


        if (this.state.alphabets[0] !== undefined) {
            console.log("state set: ", this.state.alphabets);
            let splitword = letters.map((letter, index)=>{
                return(<div><img src=""/>{letter}</div>);
            });
            display = <h1>{this.state.alphabets[0].link}</h1>
            return display
        }

        return(
            <div>
                <button>Start Game</button>
            </div>
            )
    }
}
